UDPServer.java
import java.io.*; import java.net.*; public class UDPServer implements Runnable { DatagramSocket socket = null; DatagramPacket packetOut = null; DatagramPacket packetIn = null; BufferedReader readFromKeyBoard = null; byte[] dataIn; byte[] dataOut; public UDPServer() { try { readFromKeyBoard = new BufferedReader( new InputStreamReader(System.in)); dataIn = new byte[1024]; dataOut = new byte[1024]; socket = new DatagramSocket(997); new Thread(this).start(); InetAddress inetAddress = InetAddress.getLocalHost(); while (true) { String message = readFromKeyBoard.readLine(); dataIn = message.getBytes(); packetOut = new DatagramPacket(dataIn, message.length(), inetAddress, 999); socket.send(packetOut); } } catch (Exception exp) { exp.printStackTrace(); } } public void run() { try { while (true) { packetIn = new DatagramPacket(dataOut, dataOut.length); socket.receive(packetIn); String message = new String(packetIn.getData(), 0, packetIn.getLength()); System.out.println(message); } } catch (Exception exp) { exp.printStackTrace(); } } public static void main(String[] args) { new UDPServer(); } } UDPClient.java
import java.io.*; import java.net.*; public class UDPClient implements Runnable { DatagramSocket socket = null; DatagramPacket packetOut = null; DatagramPacket packetIn = null; BufferedReader readFromKeyBoard = null; byte[] dataIn; byte[] dataOut; public UDPClient() { try { readFromKeyBoard = new BufferedReader( new InputStreamReader(System.in)); dataIn = new byte[1024]; dataOut = new byte[1024]; socket = new DatagramSocket(999); new Thread(this).start(); InetAddress inetAddress = InetAddress.getLocalHost(); while (true) { String message = readFromKeyBoard.readLine(); dataIn = message.getBytes(); packetOut = new DatagramPacket(dataIn, message.length(), inetAddress, 997); socket.send(packetOut); } } catch (Exception exp) { exp.printStackTrace(); } } public void run() { try { while (true) { packetIn = new DatagramPacket(dataOut, dataOut.length); socket.receive(packetIn); String message = new String(packetIn.getData(), 0, packetIn.getLength()); System.out.println(message); } } catch (Exception exp) { exp.printStackTrace(); } } public static void main(String[] args) { new UDPClient(); } }

Method 1: Using Thread class
class Threading { public static void main(String[] args) { /*Method No.1*/ Counter ctr = new Counter(); ctr.start(); } } /*Method No.1*/ class Counter extends Thread { public void run() { int ctr = 1; while (ctr &lt;= 10) { System.out.println(ctr++); try { sleep(1000); } catch (InterruptedException ex) {} } } } Method 2: Using Runnable interface
class Threading { public static void main(String[] args) { /*Method No.2*/ Thread thread = new Thread(new Counter()); thread.start(); } } /*Method No.2*/ class Counter implements Runnable { public void run() { int ctr = 1; while (ctr &lt;= 10) { System.out.println(ctr++); try { Thread.sleep(1000); } catch (InterruptedException ex) {} } } }
