import java.io.BufferedReader; import java.io.IOException; import java.io.InputStreamReader; import java.util.Collection; import java.util.Iterator; import org.jivesoftware.smack.Chat; import org.jivesoftware.smack.ConnectionConfiguration; import org.jivesoftware.smack.MessageListener; import org.jivesoftware.smack.Roster; import org.jivesoftware.smack.RosterEntry; import org.jivesoftware.smack.XMPPConnection; import org.jivesoftware.smack.XMPPException; import org.jivesoftware.smack.packet.Message; /** * * @author dhanoopbhaskar */ public class GtalkClient implements Runnable, MessageListener { ConnectionConfiguration connConfig = null; XMPPConnection xMPPConnection = null; BufferedReader readFromKeyboard = null; String toAddresss = null; String[] buddies = null; int buddySize = 0; public GtalkClient() { /** * Set the &#39;ConnectionConfiguration&#39; with * -host name * -port number * -service name */ connConfig = new ConnectionConfiguration(&#34;talk.google.com&#34;, 5222, &#34;gmail.com&#34;); /** * Create an instance of &#39;XMPPConnection&#39; with the already created * instance of &#39;ConnectionConfiguration&#39; */ xMPPConnection = new XMPPConnection(connConfig); try { /** * Connecting to the service */ xMPPConnection.connect(); /** * Login to the GMail account from which you want to chat * Provide * -email id * -password */ xMPPConnection.login(&#34;user-name@gmail.com&#34;, &#34;password&#34;); } catch (XMPPException ex) { System.out.println(&#34;Error: &#34; + ex.getMessage()); } /** * BufferedReader to read from the keyboard */ readFromKeyboard = new BufferedReader(new InputStreamReader(System.in)); displayBuddyList(); System.out.println(&#34;

Enter the recipient&#39;s Email Id! or &#34; + &#34;buddy id in the list&#34;); try { String temp = readFromKeyboard.readLine(); try { int j = Integer.parseInt(temp); toAddresss = getBuddy(j); System.out.println(&#34;Buddy &lt;&#34; + toAddresss + &#34;&gt; selected!&#34;); } catch(NumberFormatException exp) { toAddresss = temp; } } catch (IOException ex) { System.out.println(&#34;Error: &#34; + ex.getMessage()); } System.out.println(&#34;Enter your chat messages one by one!&#34;); System.out.println(&#34;[Enter \&#34;quit\&#34; to end the chat!]&#34;); String msg = &#34;&#34;; while(true) { try { msg = readFromKeyboard.readLine(); } catch (IOException ex) { System.out.println(&#34;Error: &#34; + ex.getMessage()); } if(msg.equalsIgnoreCase(&#34;quit&#34;)) { System.out.println(&#34;--Chat Ended--&#34;); break; } else { sendMessage(toAddresss, msg); } } } /** * * @param recipient * @param message */ private void sendMessage(String recipient, String message) { /** * Create an instance of &#39;Chat&#39; providing the recipient&#39;s email-id * and an instance of MessageListener interface(The predefined * reference &#39;this&#39; will do, since the class * implements the MessageListener interface. */ Chat chat = xMPPConnection.getChatManager().createChat(recipient, this); try { /** * Sending the chat message */ chat.sendMessage(message); } catch (XMPPException ex) { System.out.println(&#34;Error: &#34; + ex.getMessage()); } } /** * This method belongs to MessageListener interface. * It listens for the incoming chat messages. */ @Override public void processMessage(Chat chat, Message msg) { String msgStr = msg.getBody(); System.out.println(&#34;&lt;&#34; + chat.getParticipant() + &#34;&gt; says &#34; + msgStr); } public static void main(String[] args) { GtalkClient gtalkClient = new GtalkClient(); } @Override public void run() { } private void displayBuddyList() { Roster roster = xMPPConnection.getRoster(); Collection entries = roster.getEntries(); System.out.println(&#34;

------------Your Buddies!!------------&#34;); System.out.println(entries.size() + &#34; buddy(ies):
&#34;); Iterator iter = entries.iterator(); buddySize = entries.size(); buddies = new String[buddySize]; int i = 0; while (iter.hasNext()) { RosterEntry rosterEntry = (RosterEntry) iter.next(); buddies[i] = rosterEntry.getUser(); i++; System.out.println(i + &#34;. &#34; + rosterEntry.getUser()); } System.out.println(&#34;--------------------------------------&#34;); } private String getBuddy(int i) { String buddy = &#34;&#34;; if(i &gt; 0 &amp;&amp; i &lt;= buddySize) { buddy = buddies[i-1]; } else { System.out.println(&#34;Invalid Buddy Id!! Selected default one!!&#34;); buddy = buddies[0]; } return buddy; } } &mdash;&mdash;Sample Output&mdash;&mdash;
------------Your Buddies!!------------ 3 buddy(ies): 1. dhanoopbhaskar@gmail.com 2. dhanoopbhaskar4@gmail.com 3. dhanoopbhaskar3@gmail.com -------------------------------------- Enter the recipient's Email Id! or buddy id in the list 1 Buddy &lt;dhanoopbhaskar@gmail.com&gt; selected! Enter your chat messages one by one! [Enter &quot;quit&quot; to end the chat!] hi &lt;dhanoopbhaskar@gmail.com&gt; says hello quit --Chat Ended--  NB: To run and test the above program you need to download Smack XMPP API from here&hellip;

